+        limbs[1] = (b1 & ~mask) | (c1 & mask);
+        limbs[2] = (b2 & ~mask) | (c2 & mask);
+        limbs[3] = (b3 & ~mask) | (c3 & mask);
+        limbs[4] = (b4 & ~mask) | (c4 & mask);
