+        limbs[i - 1] += (v << 20) & LIMB_MASK;
+
+        // 2^(52i-52-12)
+        limbs[i - 2] -= (v << 40) & LIMB_MASK;
+        limbs[i - 1] -= v >> 12;
+
+        // 2^(52i-3*52-4)
+        limbs[i - 4] -= (v << 48) & LIMB_MASK;
+        limbs[i - 3] -= v >> 4;
+
+        // 2^(52i-4*52-48)
+        limbs[i - 5] += (v << 4) & LIMB_MASK;
+        limbs[i - 4] += v >> 48;
