+            if (destination != null) {
+                Link link = new Link(destination, null);
+                Text text = new Text(content);
+                text.setSourceSpans(textSource.getSourceSpans());
+                link.appendChild(text);
+                return ParsedInline.of(link, scanner.position());
+            }
+        }
+        return ParsedInline.none();
+    }
