+    public static Scanner of(SourceLines lines) {
+        return new Scanner(lines.getLines(), 0, 0);
+    }
+
+    public char peek() {
+        if (index < lineLength) {
+            return line.getContent().charAt(index);
+        } else {
+            if (lineIndex < lines.size() - 1) {
+                return '\n';
+            } else {
