+
+    public void add(NodeRenderer nodeRenderer) {
+        for (Class<? extends Node> nodeType : nodeRenderer.getNodeTypes()) {
+            // Overwrite existing renderer
+            renderers.put(nodeType, nodeRenderer);
+        }
