+                    // Already percent-encoded, preserve
+                    sb.append(input);
+                } else {
+                    // %25 is the percent-encoding for %
+                    sb.append("%25");
+                    sb.append(input, 1, input.length());
+                }
+            } else {
+                byte[] bytes = input.getBytes(Charset.forName("UTF-8"));
+                for (byte b : bytes) {
+                    sb.append('%');
+                    sb.append(HEX_DIGITS[(b >> 4) & 0xF]);
+                    sb.append(HEX_DIGITS[b & 0xF]);
