+     */
+    public static boolean scanLinkDestination(Scanner scanner) {
+        if (!scanner.hasNext()) {
+            return false;
+        }
+
+        if (scanner.next('<')) {
+            while (scanner.hasNext()) {
+                switch (scanner.peek()) {
+                    case '\\':
+                        scanner.next();
+                        if (isEscapable(scanner.peek())) {
+                            scanner.next();
+                        }
+                        break;
+                    case '\n':
+                    case '<':
+                        return false;
+                    case '>':
+                        scanner.next();
+                        return true;
+                    default:
