+    public static boolean scanLinkTitle(Scanner scanner) {
+        if (!scanner.hasNext()) {
+            return false;
+        }
+
+        char endDelimiter;
+        switch (scanner.peek()) {
+            case '"':
+                endDelimiter = '"';
+                break;
+            case '\'':
+                endDelimiter = '\'';
+                break;
+            case '(':
+                endDelimiter = ')';
+                break;
+            default:
+                return false;
+        }
+        scanner.next();
+
+        if (!scanLinkTitleContent(scanner, endDelimiter)) {
+            return false;
+        }
+        if (!scanner.hasNext()) {
+            return false;
+        }
+        scanner.next();
+        return true;
+    }
