+        // Quick check to see if we have enough delimiters.
+        int delimiterCount = scanner.matchMultiple(delimiterChar);
+        if (delimiterCount < delimiterProcessor.getMinLength()) {
+            scanner.setPosition(start);
+            return null;
+        }
