+        boolean hasContent = false;
+        int length = line.length();
+        for (int i = indexAfterMarker; i < length; i++) {
+            char c = line.charAt(i);
+            if (c == '\t') {
+                contentColumn += Parsing.columnsToNextTabStop(contentColumn);
+            } else if (c == ' ') {
+                contentColumn++;
+            } else {
+                hasContent = true;
+                break;
+            }
+        }
