+            case '"':
+                endDelimiter = '"';
+                break;
+            case '\'':
+                endDelimiter = '\'';
+                break;
+            case '(':
+                endDelimiter = ')';
+                break;
+            default:
+                return false;
+        }
+        scanner.next();
+
+        if (!scanLinkTitleContent(scanner, endDelimiter)) {
+            return false;
+        }
+        if (!scanner.hasNext()) {
+            return false;
+        }
+        scanner.next();
+        return true;
+    }
+
+    public static boolean scanLinkTitleContent(Scanner scanner, char endDelimiter) {
+        while (scanner.hasNext()) {
+            char c = scanner.peek();
+            if (c == '\\') {
+                scanner.next();
+                if (isEscapable(scanner.peek())) {
+                    scanner.next();
+                }
+            } else if (c == endDelimiter) {
+                return true;
+            } else if (endDelimiter == ')' && c == '(') {
+                // unescaped '(' in title within parens is invalid
+                return false;
+            } else {
+                scanner.next();
+            }
+        }
+        return true;
+    }
+
+    // spec: a nonempty sequence of characters that does not start with <, does not include ASCII space or control
+    // characters, and includes parentheses only if (a) they are backslash-escaped or (b) they are part of a balanced
+    // pair of unescaped parentheses
+    private static boolean scanLinkDestinationWithBalancedParens(Scanner scanner) {
+        int parens = 0;
+        boolean empty = true;
+        while (scanner.hasNext()) {
+            char c = scanner.peek();
+            switch (c) {
+                case ' ':
+                    return !empty;
+                case '\\':
+                    scanner.next();
+                    if (isEscapable(scanner.peek())) {
+                        scanner.next();
+                    }
+                    break;
+                case '(':
+                    parens++;
+                    // Limit to 32 nested parens for pathological cases
+                    if (parens > 32) {
+                        return false;
+                    }
+                    scanner.next();
+                    break;
+                case ')':
+                    if (parens == 0) {
+                        return true;
+                    } else {
+                        parens--;
+                    }
+                    scanner.next();
+                    break;
+                default:
+                    // or control character
+                    if (Character.isISOControl(c)) {
+                        return !empty;
+                    }
+                    scanner.next();
+                    break;
+            }
+            empty = false;
+        }
+        return true;
+    }
+
+    private static boolean isEscapable(char c) {
+        switch (c) {
+            case '!':
+            case '"':
+            case '#':
+            case '$':
+            case '%':
+            case '&':
+            case '\'':
+            case '(':
+            case ')':
+            case '*':
+            case '+':
+            case ',':
+            case '-':
+            case '.':
+            case '/':
+            case ':':
+            case ';':
+            case '<':
+            case '=':
+            case '>':
+            case '?':
+            case '@':
+            case '[':
+            case '\\':
+            case ']':
+            case '^':
+            case '_':
+            case '`':
+            case '{':
+            case '|':
+            case '}':
+            case '~':
+                return true;
+        }
+        return false;
+    }
+}

