+            textContent.writeStripped(indentedCodeBlock.getLiteral());
+            writeEndOfLineIfNeeded(indentedCodeBlock, null);
+        } else {
+            textContent.write(indentedCodeBlock.getLiteral());
+        }
+    }
+
+    @Override
+    public void visit(Link link) {
