+
+  static bool run_test() {
+
+    _success = false;
+    _assertion_failures = 0;
+
+    ShenandoahSimpleBitMap bm_small(SMALL_BITMAP_SIZE);
+    ShenandoahSimpleBitMap bm_large(LARGE_BITMAP_SIZE);
