+
+    _success = true;
+    return true;
+  }
+
+};
+
+TEST(BasicShenandoahSimpleBitMapTest, minimum_test) {
+
+  bool result = ShenandoahSimpleBitMapTest::run_test();
+  ASSERT_EQ(result, true);
+  ASSERT_EQ(_success, true);
+  ASSERT_EQ(_assertion_failures, (size_t) 0);
