+class ResolvedPutFieldHelper {
+   int x; // change it to 'final'
+   ResolvedPutFieldHelper() { x = 1; }
+   void set_x() { x = 2; }
+
+    public static void main(String args[]) {
+        ResolvedPutFieldHelper s = new ResolvedPutFieldHelper();
+        s.set_x();
+        System.out.println(s.x);
+    }
+}
