+            System.out.println(eventToText(e));
+        }
+        System.out.println();
+        System.out.println("Outer stream:");
+        for (String s : outerStream) {
+            System.out.println(s);
+        }
+        System.out.println("Inner stream:");
+        for (String s : innerStream) {
+            System.out.println(s);
+        }
+    }
