+                swapLastTwoBlocks(ciphertextBuf, ciphertextEnd, pad, blockSize);
+            } else {
+                // .... ffff pp[pp] -> .... pp[pp] ffff
+                swapLastTwoBlocks(ciphertextBuf, ciphertextEnd, blockSize, pad);
+            }
