+        private static List<Class<?>> valueTypeFields(Class<?> type) {
+            List<Class<?>> result = new ArrayList<>();
+            Arrays.stream(type.getDeclaredFields())
+                  .filter(f -> !Modifier.isStatic(f.getModifiers()))
+                  .map(f -> f.getType())
+                  .filter(ft -> ft.isValue() && !result.contains(ft))
+                  .forEach(result::add);
