+        // ------ utility methods ------
+        private static boolean eq(Object a, Object b)   {
+            if (a == null && b == null) return true;
+            if (a == null || b == null) return false;
+            if (a.getClass() != b.getClass()) return false;
+            return a.getClass().isValue() ? valueEquals(a, b) : (a == b);
+        }
