+        _original_loop_entry(loop->_head->as_Loop()->skip_strip_mined()->in(LoopNode::EntryControl)),
+        _flat_array_check_candidates(),
+        _candidate(find_unswitch_candidate(loop)) {}
+  NONCOPYABLE(UnswitchCandidate);
