+        // POSITIVE TESTS
+        // --------------
+
+        // Base case
+        Child c = new Child();
+        System.out.println(c);
+
+        // Constructor with control flow
+        ControlFlowChild c1 = new ControlFlowChild(true, true);
+        System.out.println(c1);
