+void FieldLayout::shift_fields(int shift) {
+  LayoutRawBlock* b = first_field_block();
+  LayoutRawBlock* previous = b->prev_block();
+  if (previous->block_kind() == LayoutRawBlock::EMPTY) {
+    previous->set_size(previous->size() + shift);
