+    nb->set_offset(b->offset());
+    previous->set_next_block(nb);
+    nb->set_prev_block(previous);
+    b->set_prev_block(nb);
+    nb->set_next_block(b);
+  }
+  while (b != nullptr) {
+    b->set_offset(b->offset() + shift);
+    if (b->block_kind() == LayoutRawBlock::REGULAR || b->block_kind() == LayoutRawBlock::FLAT) {
