+void x86_generate_icache_fence(MacroAssembler* _masm) {
+  switch (X86ICacheSync) {
+    case 0:
+      break;
+    case 1:
+      __ mfence();
+      break;
+    case 2:
+    case 3:
+      __ sfence();
+      break;
+    case 4:
+      __ push(rax);
+      __ push(rbx);
+      __ push(rcx);
+      __ push(rdx);
+      __ xorptr(rax, rax);
+      __ cpuid();
+      __ pop(rdx);
+      __ pop(rcx);
+      __ pop(rbx);
+      __ pop(rax);
+      break;
+    case 5:
+      __ serialize();
+      break;
+    default:
+      ShouldNotReachHere();
+  }
+}
+
+void x86_generate_icache_flush_insn(MacroAssembler* _masm, Register addr) {
+  switch (X86ICacheSync) {
+    case 1:
+      __ clflush(Address(addr, 0));
+      break;
+    case 2:
+      __ clflushopt(Address(addr, 0));
+      break;
+    case 3:
+      __ clwb(Address(addr, 0));
+      break;
