+static void load4regs(int destRegs[], Register address, int offset,
+                      MacroAssembler *_masm) {
+  for (int i = 0; i < 4; i++) {
+    __ evmovdquw(xmm(destRegs[i]), Address(address, offset + i * 64),
+                 Assembler::AVX_512bit);
+  }
