+  store4regs(coeffs, 0, xmm8_11, _masm);
+  store4regs(coeffs, 256, xmm12_15, _masm);
+
+  __ leave(); // required for proper stackwalking of RuntimeStub frame
+  __ mov64(rax, 0); // return 0
+  __ ret(0);
+
+  return start;
+}
