+        name = v;
+        return this;
+    }
+
+    ApplicationBuilder description(String v) {
+        description = v;
+        return this;
+    }
+
+    ApplicationBuilder version(String v) {
+        version = v;
+        return this;
+    }
+
+    ApplicationBuilder vendor(String v) {
+        vendor = v;
+        return this;
+    }
+
+    ApplicationBuilder copyright(String v) {
