+abstract class PackagerBuilder<T extends Package, U extends PackagerBuilder<T, U>> {
+
+    U pkg(T v) {
+        pkg = v;
+        return thiz();
+    }
+
+    U env(BuildEnv v) {
+        env = v;
+        return thiz();
+    }
+
