+                CtorTest.create(2, 2),
+                CtorTest.create(7, 4),
+                CtorTest.createFail(0, 1),
+                CtorTest.createFail(1, 0),
+                CtorTest.createFail(-1, 1),
+                CtorTest.createFail(1, -1),
+                CtorTest.createFail(0, 0),
+                CtorTest.createFail(-3, -9)
+        );
+    }
