+
+    interface I extends RequiresIdentityHelper.RequiresIdentityInt<Integer> {} // should warn
+
+    void m(Object o) {
+        RequiresIdentityHelper<?> ri = (RequiresIdentityHelper<Integer>) o; // should warn
