+        private TemplateToken generateTestFields(String[] invarRest, String[] containerNames, String[] indexFormNames) {
+            var template = Template.make(() -> body(
+                let("ivType", isLongIvType ? "long" : "int"),
+                """
+                // invarRest fields:
