-      closure.do_addr(cur_addr, obj_size);
+
+      size_t remaining_words = pointer_delta(end_addr, cur_addr);
+      markWord mark = safe_mark_prototype(cur_addr, remaining_words);
+
+      closure.do_addr(cur_addr, obj_size, mark);
+
