+    field_val = phi->try_push_inline_types_down(&_igvn, true);
+    if (field_val->is_InlineType()) {
+      value_worklist->push(field_val);
+    }
+  }
+  DEBUG_ONLY(verify_type_compatability(field_val->bottom_type(), field_type);)
+  sfpt->add_req(field_val);
+}
