+import pkg.Foo;
+
+public class SeparateCompilationTest {
+    void test(Foo<String> foo) {
+        foo.s = null;
+        foo.s_arr = null;
+        foo.x = null;
+        foo.x_arr = null;
+        foo.list_x = null;
+    }
