+  Node* alloc_mem = call->in(TypeFunc::Memory);
+  if (alloc_mem == NULL || alloc_mem->is_top()) {
+    return false;
+  }
+  if (!alloc_mem->is_MergeMem()) {
+    alloc_mem = MergeMemNode::make(alloc_mem);
+    igvn->register_new_node_with_optimizer(alloc_mem);
+  }
