+                continue;
+            Content fieldsContent = fieldWriter.getFieldsContentHeader(tag.equals(tags.last()));
+            TypeMirror type = ch.getReferencedType(tag);
+            fieldWriter.addMemberHeader(type, tag.getName().getName().toString(), fieldsContent);
