+  ciMultiField(ciInstanceKlass* klass, int index, Bytecodes::Code bc) : ciField(klass, index, bc) {}
+  ciMultiField(fieldDescriptor* fd, bool bundled) : ciField(fd, bundled) {
+    _is_multifield_base = true;
